function AddMessage(priority, loglevel, message) {
    if (priority <= loglevel) {
        node.status(message);
    }
}

let settings = flow.get("settings");
if (settings === undefined) {
    AddMessage(0,0,{fill:"red",shape:"ring",text:"Missing settings"});
    return;
}
// Set settings defaults
if (settings.rulestore === undefined) settings.rulestore = "";
if (settings.loglevel === undefined) settings.loglevel = 6;

let rules = flow.get("rules", settings.rulestore) ?? [];
let datacache = flow.get("datacache") ?? {};

if ((msg.topic === "data") || (msg.topic === "time")) {
    AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "0| Input type: " + msg.topic });
    if (msg.topic === "data") {
        datacache[msg.payload.id] = msg.payload;
        flow.set("datacache", datacache);
    }


    for (let i = 0; i < rules.length; i++) {
        let rule = rules[i];
        AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "0| Checking " + rule.name });
        if (!rule.enabled) {
            // Rule is not enabled, skip to the next rule
            AddMessage(3, settings.loglevel,{ fill: "green", shape: "dot", text: "1a| Rule " + rule.name + " not enabled." });
            continue;
        } 

        // Check if the current data is listed in the conditions
        if (((msg.topic === "data") && (rule.conditions.filter(e => e.operand === msg.payload.id).length > 0)) || ((msg.topic === "time") && (rule.conditions.filter(e => e.trigger === "time").length > 0))) {
            AddMessage(4, settings.loglevel,{fill:"grey",shape:"dot",text:"1| Evaluating rule "+rule.name});
            let condition_or = false;
            let condition_and = true;
            // evaluate conditions
            for (let j = 0; j < rule.conditions.length; j++) {
                let condition = rule.conditions[j];
                AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "2| Check condition " + j + ", trigger:" +condition.trigger});
                let data = datacache[condition.operand];
                switch (condition.trigger) {
                    case "time": 
                        // Data validation
                        if (condition.value.length !== 5) {
                            AddMessage(1, settings.loglevel,{ fill: "red", shape: "dot", text: "3| Date format incorrect (length)" });
                            condition_and = false;
                            condition_or = false;
                            break;
                        }
                        if (condition.value.split(":").length !== 2) {
                            AddMessage(1, settings.loglevel,{ fill: "red", shape: "dot", text: "3| Date format incorrect (hh:mm)" });
                            condition_and = false;
                            condition_or = false;
                            break;
                        }
                        // Construct the current time
                        let now = new Date();
                        let hour = "00000" + now.getHours();
                        let minute = "00000" + now.getMinutes();
                        hour = hour.substring(hour.length - 2, hour.length);
                        minute = minute.substring(minute.length - 2, minute.length);

                        data = { "id": "time", "name": "time", "type": "string", "value": hour + ":" + minute };
                        break;
                    case "data":
                    case "state":
                        // if data does not exists, evaluate to true - most to allow initial states
                        if (data === undefined) {
                            condition_or = true;
                            AddMessage(3, settings.loglevel,{ fill: "grey", shape: "dot", text: "2| " + condition.operand + " data missing, evaluating to true" });
                            break;
                        }
                        break;
                    default: AddMessage(1, settings.loglevel,{ fill: "red", shape: "ring", text: "4| Unknown condition trigger " + condition.trigger });

                }

                let breakout = false;
                if (data !== undefined) {
                    AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "3| " + condition.operand + " condition: " + data.value+ " " + condition.operator + " "+ condition.value});

                    switch(condition.operator) {
                        case "GT": 
                            if (data.value > condition.value) {
                                condition_or = true;
                                AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "4| " + condition.operand + " condition true" });
                            } else {
                                condition_and = false;
                                AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "4| " + condition.operand + " condition false" });
                                if (rule.operator === "AND") {
                                    breakout = true;
                                }
                            }
                            break;
                        case "LT":
                            if (data.value < condition.value) {
                                condition_or = true;
                                AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "4| " + condition.operand + " condition true" });
                            } else {
                                condition_and = false;
                                AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "4| " + condition.operand + " condition false" });
                                if (rule.operator === "AND") {
                                    breakout = true;
                                }
                            }
                            break;
                        case "EQ":
                            if (data.value === condition.value) {
                                condition_or = true;
                                AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "4| " + condition.operand + " condition true" });
                            } else {
                                condition_and = false;
                                AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "4| " + condition.operand + " condition false" });
                                if (rule.operator === "AND") {
                                    breakout = true;
                                }
                            }
                            break;
                        default: AddMessage(1, settings.loglevel,{ fill: "red", shape: "ring", text: "4| Unknown condition operator "+ condition.operator});
                    } // end switch
                }
                // Check if we need to break out from the loop (if a condition failed already and we are in AND mode)
                if (breakout) {
                    AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "4a| Exit condition checking" });
                    break;
                }
            } // end for j loop
            // All the rules are evaluated
            let passed = false;
            if ((rule.operator === "AND") && (condition_and)) { passed = true }
            if ((rule.operator === "OR") && (condition_or)) { passed = true }
            if (passed) {
                AddMessage(3, settings.loglevel, { fill: "green", shape: "dot", text: "5| " + rule.name + ": Condition result: " + passed });
            } else {
                AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "5| " + rule.name + ": Condition result: " + passed});
            }

            // Execute the actions
            if (passed) { 
                for (let k=0; k<rule.actions.length; k++) { 
                    let action = rule.actions[k];
                    AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "6| Starting action " + action.type });
                    switch (action.type) {
                        case "setstate":
                            datacache[action.state] = {"id": action.state, "name": action.name, "type": "state", "value": action.value}
                            flow.set("datacache", datacache);
                            AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "7| Status set:" + action.state });
                            break;
                        case "control":
                            node.send({"topic": action.topic, "payload": action.value});
                            AddMessage(4, settings.loglevel,{ fill: "grey", shape: "dot", text: "7| Control sent:" + action.topic });
                            break;
                        default: AddMessage(1, settings.loglevel,{ fill: "red", shape: "ring", text: "7| Unknown action type " + action.type });
                    }
                } // End of action loop
            } // End of actions 

        } // Condition was found
    } // End of looping through the rules


} // End of processing data message

