function moveup(array, from) {
    let top = array[from-1];
    array[from-1] = array[from];
    array[from] = top;
    return array;
}

function movedown(array, from) {
    let down = array[from + 1];
    array[from + 1] = array[from];
    array[from] = down;
    return array;
}

