// Node configuration
let count = 1000;
let data = context.get("tinygs") || [];
let now = new Date();

// Update the status with current timestamp
var yyyy = now.getFullYear();
var mm = now.getMonth() < 9 ? "0" + (now.getMonth() + 1) : (now.getMonth() + 1); // getMonth() is zero-based
var dd  = now.getDate() < 10 ? "0" + now.getDate() : now.getDate();
var hh = now.getHours() < 10 ? "0" + now.getHours() : now.getHours();
var mmm  = now.getMinutes() < 10 ? "0" + now.getMinutes() : now.getMinutes();
var ss  = now.getSeconds() < 10 ? "0" + now.getSeconds() : now.getSeconds();
let formattedtime = dd + "." + mm + "." + yyyy + " " + hh + ":" + mmm + ":" + ss;

switch (msg.topic) {
    case "reset":
        data = [];
        context.set("tinygs",data);
        break;
    case "flush":
        return [{payload:data}];
    default:
        if (data.length>=count) {
            data.shift();
        } 
        data.push({"timestamp": now.getTime(), "formattedtime": formattedtime, "topic": msg.topic, "payload":msg.payload});
        context.set("tinygs",data);
        node.status({fill:"green",shape:"ring",text:"Items stored: "+data.length});  
        return [{payload:data}];
}


node.status({fill:"green",shape:"ring",text:"Items stored: "+data.length});

// This is the data for the first line of the telegram message:
// Norby, RSSI/SNR:-130.5/-12.5dB, CONFIRMED

msg.msg1 = "üì° "+msg.payload[0][4]+", "+msg.payload[2][4]+", "+msg.payload[4][4];



return msg;

// msg.url generates the URL to download the packet information
// msg.filename generates the filename, so the entire packet is saved in a json file

msg.url = "https://api.tinygs.com/v1/packet/" + msg.payload.packets[0].id;
msg.filename = "/home/nygma/tinygs/"+ msg.payload.packets[0].id +".json";


return msg;

let station_id="YOUR_STATION_ID";

// This code finds the data for your station from the stations arra and
// saved it in msg.payload.mystation
for (let i=0; i<msg.payload.stations.length; i++) {
    if (msg.payload.stations[i].name === station_id) {
        msg.payload.mystation = msg.payload.stations[i];
    }
}

return msg;

let template = msg.payload;

template = template.substring(template.indexOf(">")+3,1000);
template = template.replace(/<br \/>/g, "");
template = template.substring(0,template.indexOf("<"));
template = template.replace(/{{p./g, "{{msg.payload.parsed.payload.");

msg.payload = template;
return msg;

// This generates the second line of the telegram message:
// YOUR_STATION_ID: 1741 km, 11.16¬∞, -128.25 dBm, -11.25 dB

msg.msg2 = msg.payload.mystation.name+": "+msg.payload.mystation.distance.toFixed(0)+" km, "+msg.payload.mystation.elevation.toFixed(2)+"¬∞, "+msg.payload.mystation.receptionParams.rssi+" dBm, "+msg.payload.mystation.receptionParams.snr+" dB";
return msg;

// we get the HTML template from the resonse and make some changes to it
// so we can run it through the template node 

let template = msg.payload.template;

template = template.substring(template.indexOf(">")+3,1000);
template = template.replace(/<br \/>/g, "");
template = template.substring(0,template.indexOf("<"));
template = template.replace(/{{p./g, "{{payload.parsed.payload.");

msg.template = template;
return msg;

// Put together the entire telegram message
// 1st line
// 2nd line
// template with the full data

msg.payload = {service: "18", type:"message", content:msg.msg1+"\r\n"+msg.msg2+"\r\n"+msg.payload};
return msg;

let station_id="YOUR_STATION_ID";

// get the data on my station and store it in msg.payload.mystation
for (let i=0; i<msg.payload.stations.length; i++) {
    if (msg.payload.stations[i].name === station_id) {
        msg.payload.mystation = msg.payload.stations[i];
    }
}

msg.topic = "INSERT INTO tinygs_msg (serverTime, satellite, satDisplayName, freq, id, lat, lng, alt, station, crc_error, snr, frequency_error, rssi, distance, elevation, doppler)" +
  " VALUES(" +
  msg.payload.serverTime+", "+
  "'"+msg.payload.satellite+"', "+
  "'"+msg.payload.satDisplayName+"', "+
  msg.payload.freq+", "+
  "'"+msg.payload.id+"', "+
  msg.payload.satPos.lat+", "+
  msg.payload.satPos.lng+", "+
  msg.payload.satPos.alt+", "+
  "'"+msg.payload.mystation.name+"', "+
  (msg.payload.mystation.crc_error ? 1 : 0)+", "+
  msg.payload.mystation.receptionParams.snr+", "+
  msg.payload.mystation.receptionParams.frequency_error+", "+
  msg.payload.mystation.receptionParams.rssi+", "+
  msg.payload.mystation.distance+", "+
  msg.payload.mystation.elevation+", "+
  msg.payload.mystation.doppler+
  ")";

return msg;

// Node configuration
let count = 100;
let data = context.get("errors") || [];
let now = new Date();

// Update the status with current timestamp
var yyyy = now.getFullYear();
var mm = now.getMonth() < 9 ? "0" + (now.getMonth() + 1) : (now.getMonth() + 1); // getMonth() is zero-based
var dd  = now.getDate() < 10 ? "0" + now.getDate() : now.getDate();
var hh = now.getHours() < 10 ? "0" + now.getHours() : now.getHours();
var mmm  = now.getMinutes() < 10 ? "0" + now.getMinutes() : now.getMinutes();
var ss  = now.getSeconds() < 10 ? "0" + now.getSeconds() : now.getSeconds();
let formattedtime = dd + "." + mm + "." + yyyy + " " + hh + ":" + mmm + ":" + ss;

switch (msg.topic) {
    case "reset":
        data = [];
        context.set("errors",data);
        break;
    case "flush":
        return [{payload:data}];
    default:
        if (data.length>=count) {
            data.shift();
        } 
        data.push({"timestamp": now.getTime(), "formattedtime": formattedtime, "topic": msg.topic, "payload":msg.payload});
        context.set("errors",data);
        node.status({fill:"green",shape:"ring",text:"Items stored: "+data.length});  
        return [{payload:data}];
}


node.status({fill:"green",shape:"ring",text:"Items stored: "+data.length});

// Save data from the MQTT message for the Telegram message
let myarr = msg.topic.split("/");
msg.msg1 = "üì° "+ myarr[2]+ " - next satellite\r\nüõ∞ " + msg.payload.sat;

let norad     =msg.payload.NORAD;
let nextdays  = "1";
let observer_lat = 47.441;
let observer_lng = 19.034;
let observer_alt = 150; 
let min_elevation = 12;
let apikey ="YOUR_API_KEY";

msg.url = "https://api.n2yo.com/rest/v1/satellite/visualpasses/"+norad +"/"+observer_lat +"/"+observer_lng +"/" +observer_alt +"/"+nextdays +"/" + min_elevation +"/&apiKey=" +apikey ;
return msg;

/*

Request:https://api.n2yo.com/rest/v1/satellite/visualpasses/25544/41.702/-76.014/0/2/300/&apiKey=589P8Q-SDRYX8-L842ZD-5Z9


Get radio passes
The "radio passes" are similar to "visual passes", the only difference being the requirement for the objects to be optically visible for observers. This function is useful mainly for predicting satellite passes to be used for radio communications. The quality of the pass depends essentially on the highest elevation value during the pass, which is one of the input parameters.

Request: /radiopasses/{id}/{observer_lat}/{observer_lng}/{observer_alt}/{days}/{min_elevation}

https://www.n2yo.com/api/#radiopasses

*/

if (msg.payload.info.passescount===0) {
    msg.msg2 = "No pass in the next 24 hours";
} else {
    let start = new Date(msg.payload.passes[0].startUTC*1000);
    let max = new Date(msg.payload.passes[0].maxUTC*1000);
    let end = new Date(msg.payload.passes[0].endUTC*1000);
    msg.msg2 = "Next pass: "+start.toLocaleTimeString("it-IT")+" - "+ end.toLocaleTimeString("it-IT")+"\r\n";
    msg.msg2 += "Max elevation: "+msg.payload.passes[0].maxEl+" @ "+max.toLocaleTimeString("it-IT");
}

msg.payload = {service: "18", type:"message", content:msg.msg1+"\r\n"+msg.msg2};
return msg;

let data = [];

for (let i=0; i<msg.payload.length; i++) {

    data.push(
        {
            "measurement": "tinygs_msg",
            "tags": {
                "satellite": msg.payload[i].satellite,
                "station": msg.payload[i].station
            },
            "fields": {
                "freq": msg.payload[i].freq,
                "id": msg.payload[i].id,
                "lat": msg.payload[i].lat,
                "lng": msg.payload[i].lng,
                "alt": msg.payload[i].alt,
                "crc_error": msg.payload[i].crc_error,
                "snr": msg.payload[i].snr,
                "frequency_error": msg.payload[i].frequency_error,
                "rssi": msg.payload[i].rssi,
                "distance": msg.payload[i].distance,
                "elevation": msg.payload[i].elevation,
                "doppler": msg.payload[i].doppler,
                "satDisplayName": msg.payload[i].satDisplayName
            },
            "timestamp": msg.payload[i].serverTime*1000000
        }
    );

}

msg.payload = data;
return msg;

let station_id="YOUR_STATION_ID";

// get the data on my station and store it in msg.payload.mystation
for (let i=0; i<msg.payload.stations.length; i++) {
    if (msg.payload.stations[i].name === station_id) {
        msg.payload.mystation = msg.payload.stations[i];
    }
}

let data = [];

data.push(
  {
    "measurement": "tinygs_msg",
    "tags": {
      "satellite": msg.payload.satellite,
      "station": msg.payload.mystation.name
    },
    "fields": {
      "freq": msg.payload.freq,
      "id": msg.payload.id,
      "lat": msg.payload.satPos.lat,
      "lng": msg.payload.satPos.lng,
      "alt": msg.payload.satPos.alt,
      "crc_error": (msg.payload.mystation.crc_error ? 1 : 0),
      "snr": msg.payload.mystation.receptionParams.snr,
      "frequency_error": msg.payload.mystation.receptionParams.frequency_error,
      "rssi": msg.payload.mystation.receptionParams.rssi,
      "distance": msg.payload.mystation.distance,
      "elevation": msg.payload.mystation.elevation,
      "doppler": msg.payload.mystation.doppler,
      "satDisplayName": msg.payload.satDisplayName
    }
  }
);



msg.payload = data;
return msg;

let text = "";
let start = 0;
let max = 0;
let end = 0;
let maxel = 0;
let ispass = false;
if (msg.payload.info.passescount === 0) {
  text = "No pass in the next 24 hours";
} else {
  start = new Date(msg.payload.passes[0].startUTC * 1000);
  max = new Date(msg.payload.passes[0].maxUTC * 1000);
  end = new Date(msg.payload.passes[0].endUTC * 1000);
  maxel = msg.payload.passes[0].maxEl;
  text = "Next pass: " + start.toLocaleTimeString("it-IT") + " - " + end.toLocaleTimeString("it-IT") + "\r\n";
  text += "Max elevation: " + maxel + " @ " + max.toLocaleTimeString("it-IT");
  ispass = true;
}

let data = [];

data.push(
  {
    "measurement": "tinygs_nextpass",
    "tags": {
    },
    "fields": {
      "satname": msg.payload.info.satname,
      "start": start,
      "max": max,
      "end": end,
      "maxel": maxel,
      "ispass": ispass,
      "text": text
    }
  }
);

msg.payload = data;
return msg;
